import React from 'react';
import {Menu,Input,Grid,Header} from 'semantic-ui-react';
import {Link} from '../routes';

class Header2 extends React.Component {

  state = {
    inputAddress:''
  }

  search = ()=> {
    console.log('search ....');
  }
  /*
      <Menu style={{marginTop:'10px'}}>



          <Input fluid placeholder='查询合约...'
            value={this.state.inputAddress}
            onChange={event=>this.setState({inputAddress:event.target.value})}
          />
          <Link route={`/campaigns/${this.state.inputAddress}`}>
            <i className='search link icon' />
          </Link>
  */

  render() {
    return (

      <Header as='h3' style={{marginTop:'10px'}} block>

        <Grid>
          <Grid.Row columns={3}>
              <Grid.Column width={3}>
                  <div>
                      <Link route='/'>
                        <a>首页</a>
                      </Link>
                  </div>
              </Grid.Column>

              <Grid.Column width={10}>

                <div class="ui action input">
                  <input fluid type="text" placeholder="Search" />
                  <button class="ui button">Search</button>
                </div>

              </Grid.Column>

              <Grid.Column width={3}>
                  <div>
                      <Link route='/campaigns/new'>
                        <a>众筹 +</a>
                      </Link>
                  </div>
              </Grid.Column>
            </Grid.Row>
          </Grid>

      </Header>

    );
  }

}

export default Header2;
