import React from 'react';
import {Menu,Input,Grid,Header,Form,Button,Segment} from 'semantic-ui-react';
import {Link} from '../routes';

class Header2 extends React.Component {

  state = {
    inputAddress:''
  }

  search = ()=> {
    console.log('search ....');


  }
  /*
      <Menu style={{marginTop:'10px'}}>



          <Input fluid placeholder='查询合约...'
            value={this.state.inputAddress}
            onChange={event=>this.setState({inputAddress:event.target.value})}
          />
          <Link route={`/campaigns/${this.state.inputAddress}`}>
            <i className='search link icon' />
          </Link>



          <Input fluid placeholder='查询合约...'
            value={this.state.inputAddress}
            onChange={event=>this.setState({inputAddress:event.target.value})}
            action='search'
            onSubmit={this.onSubmit}
          />


          <Header as='h3' style={{marginTop:'10px'}} block>
            <Grid>
              <Grid.Row columns={3}>
                  <Grid.Column width={2}>
                      <div>
                          <Link route='/'>
                            <a>首页</a>
                          </Link>
                      </div>
                  </Grid.Column>

                  <Grid.Column width={12}>

                      <Link route={`/campaigns/${this.state.inputAddress}`}>
                        <Button floated='right' primary>查询</Button>
                      </Link>

                      <Input fluid placeholder='查询合约...'
                        value={this.state.inputAddress}
                        onChange={event=>this.setState({inputAddress:event.target.value})}
                      />
                  </Grid.Column>

                  <Grid.Column width={2}>
                      <div>
                          <Link route='/campaigns/new'>
                            <a>众筹 +</a>
                          </Link>
                      </div>
                  </Grid.Column>
                </Grid.Row>
              </Grid>

          </Header>
  */

  render() {
    return (

      <Segment fluid>
        <Header as='h3' textAlign='left'>
            <Link route='/'>
              <a>首页</a>
            </Link>
        </Header>

        <Header as='h3' style={{marginTop:'10px',marginLeft:'100px',marginRight:'100px'}} block>
            <Link route={`/campaigns/${this.state.inputAddress}`}>
              <Button floated='right' primary>查询</Button>
            </Link>

            <Input fluid placeholder='查询合约...'
              value={this.state.inputAddress}
              onChange={event=>this.setState({inputAddress:event.target.value})}
            />
        </Header>

        <Header as='h3' textAlign='right'>
            <Link route='/campaigns/new'>
              <a>众筹 +</a>
            </Link>
        </Header>

        </Segment>
    );
  }

}

export default Header2;
