  renderCampaigns(){
    const items = this.props.compaigns.map(address=>{
      return {
        header: address,
        description:
      'Leverage agile frameworks to provide a robust synopsis for high level overviews.',
        meta: <a>查看众筹</a>,
      }
    });
  }

  render() {
    return (
      <div>
        <p>hello index: campaign address : {this.props.campaign[0]}</p>
        <p>account address : {this.state.account}</p>
      </div>
    );
  }
}
// 导出
export default CampaignIndex;
