    let list = [];

  const camDesc = await Promise.all (
      campaigns.forEach(async  (address, i) => {
        console.log("address : ",address);

        const desc = await factory.methods.campaignMap(address).call();
        console.log("desc : ", desc);
        var item = {
          address : address,
          desc: desc,
        }
        list.push(item);
        console.log("+++++++list.length : ", list.length);

        /*
        console.log("+++++++what1 : ", list.length);
        list.forEach((l, j) => {
          console.log("foreach address : ",l.address);
          console.log("foreach desc : ",l.desc);
        });
        console.log("+++++++what2 : ", list.length);
        */
        return list;
      })
    )

    /*
