import React from 'react';
import {Menu,Input,Grid} from 'semantic-ui-react';
import {Link} from '../routes';

class Header extends React.Component {

  state = {
    inputAddress:''
  }

  render() {
    return (
      <Menu style={{marginTop:'10px'}} widths='16'>

        <Grid>
          <Grid.Row columns={4}>
              <Grid.Column width={4}>
                  <Menu.Item>
                    <Link route='/'>
                      <a>首页</a>
                    </Link>
                  </Menu.Item>
              </Grid.Column>

              <Grid.Column width={7}>
                    <Menu.Item>
                        <div className='ui transparent icon input'>
                          <Input placeholder='查询合约...'
                            value={this.state.inputAddress}
                            onChange={event=>this.setState({inputAddress:event.target.value})}
                          />
                          <Link route={`/campaigns/${this.state.inputAddress}`}>
                            <i className='search link icon' />
                          </Link>
                        </div>
                    </Menu.Item>
              </Grid.Column>

              <Grid.Column width={5}>
                    <Menu.Item>
                      <Link route='/campaigns/new'>
                        <a>众筹 +</a>
                      </Link>
                    </Menu.Item>
              </Grid.Column>
            </Grid.Row>
          </Grid>

      </Menu>

    );
  }

}

export default Header;
