import react,{Component} from 'react';
import factory from '../ethereum/factory';
import web3 from '../ethereum/web3';

import Web3 from 'web3';
// export default()=> {
//   return <h1>next index页面</h1>;
// }

// var web3;

if(typeof window !='undefined' && window.web3 != 'undefined') {
  web3 = new Web3(window.web3.currentProvider);
}else {
  const provider = new Web3.providers.HttpProvider(
    'https://ropsten.infura.io/v3/4d2fe19ec56d48dcb2fea4cf8b193416'
  );

  web3 = new Web3(provider);
}

window.addEventListener('load', async () => {
  var web3Provider;
   // Modern dapp browsers...
   if (window.ethereum) {
       web3Provider = window.ethereum;
       try {
           // 请求 账户授权
           await window.ethereum.enable();
           console.log('account access success.');
       } catch (error) {
           // 用户不授权时
           console.error("User denied account access")
       }
   }
   // Legacy dapp browsers...
   else if (window.web3) {   // 老版本 metamask 账户信息是公开的，2018-11之后，需要授权才能获取账户信息
       web3Provider = window.web3.currentProvider;
       console.log('!!!!!!!!!!!!!!!!!');
   }
   // Non-dapp browsers...
   else {
       console.log('Non-Ethereum browser detected. You should consider trying MetaMask!');
   }
   console.log('+++++++++++++++++');
   web3 = new Web3(web3Provider);
});


class CampaignIndex extends Component {
  async componentDidMount() {

    const address = await factory.options.address;
    console.log(address);

    const accounts = await web3.eth.getAccounts();
    console.log(accounts);

    const campaign = await factory.methods.getDeployedCampaign().call();
    console.log(campaign);
  }

  render() {
    return <div>hello index </div>;
  }
}
// 导出
export default CampaignIndex;
